import React, {useState} from 'react'
import {Link} from 'react-router-dom'
import { Button } from "antd";
import axios from 'axios'
import { useHistory } from 'react-router'

import './login.css'

const Login = () => {

    const {push} = useHistory()

    const [entrar, setEntrar] = useState({
        correo: "", 
        password: "" 
    })

    const inputChange = ({ target }) => {
        const { name, value } = target
        setEntrar({
            ...entrar,
            [name]: value
        })
    }

    const login = () => {

        axios.post("https://back-calistenia.herokuapp.com/api/login", entrar).then((response) => {

        console.log(response.data.error)

        if(response.data.error.length > 0){
            alert("Ingresar correo o contraseña correcta")
          } else{

              push('/dashboard/home')
          }  


        }) .catch(({ response }) => {
            console.log(response.data)
        })

    }

    return ( 
        <div className="login">

            <form className="loginForm">
            <h2>Calistenia APP</h2>
                <input 
                    type="text" 
                    name="correo"
                    placeholder="email" 
                    className="loginInput"
                    onChange={inputChange}    
                />
                <input 
                    type="password" 
                    name="password"
                    placeholder="password" 
                    className="loginInput" 
                    onChange={inputChange} 
                />

                <Button  onClick={login} className="loginButton" >Ingresar</Button>
                {/*to={"/dashboard/home"} */}
            </form>
        </div>

     );
}

export default Login;
